    }

    render() {
        return (
            <>
                <div className="navbar">
                    <ButtonGroup>
                        {TAG_NAMES.map(name => (<TagButton name={name} />))}
                    </ButtonGroup>
                </div>
                <div className="items-container">
                    <Item />

                </div>
            </>

        );
    }
}

//Returns a new button with a given name
class TagButton extends react.Component {
    constructor(props) {
        super(props);
        this.name = props.name;
    }
    render() {
        return (
            <Button className="mx-2 rounded-3" size="sm" variant="outline-info">{this.name}</Button>
        );
    }
}


class Item extends react.Component {
    constructor(props) {
        super(props);
        this.state = {
            tag: props.tag,
            title: props.title,
            desc: props.desc,

        };
    }

    render() {
        return (
            <div className="item">
                <div className="item-header">

                </div>
                <div className="item-main">

                </div>
                <div className="item-footer">

                </div>
            </div>
        );
    }
}

export default Home;
