import './styles/App.css';
import react from 'react';
import * as Icons from 'react-bootstrap-icons'
import { Link, Route, Routes, Router } from 'react-router-dom'

class App extends react.Component {

  constructor(props) {
    super(props)
    this.iconSize = 32;
  }

  render() {
    return (
      <div className="container-fluid app">
        <div className="row app-row">
          <div className="col-1 menu-container">
            <div className="app-name">
              <h1>Journaly</h1>
            </div>
            <div className="menu">
              <Router>
                <Menu />
                <Routes>
                  <Route path='/' />
                </Routes>
              </Router>
            </div>
          </div>
          <div className="col-11 journal-container">
            <header className="date">
              <Date />
            </header>
          </div>
        </div>
      </div >
    );
  }
}


class List extends react.Component {
  // constructor(props) {
  //   this.state = {

  //   }
  // }
  render() {
    return (
      <div className="list">
        <p>List 1</p>
      </div>
    );
  }
}

class Menu extends react.Component {
  constructor(props) {
    super(props);
  }

  render() {
    return (
      <>
        <div className="icon-container">
          <Link to='/'>
            <Icons.HouseFill size={this.iconSize} />
          </Link>

        </div>
        <div className="icon-container">
          <Link to='/'>
            <Icons.CalendarDateFill size={this.iconSize} />
          </Link>
        </div>
        <div className="icon-container">
          <Link to='/'>
            <Icons.ListTask size={this.iconSize} />
          </Link>
        </div>
        <div className="icon-container">
          <Link to='/'></Link>
          <Icons.PersonFill size={this.iconSize} />
        </div>
      </>
    );
  }
}

class Date extends react.Component {
  constructor(props) {
    super(props);
    this.state = {
      currentDate: 'mm/dd/yyyy'
    };
  }

  componentDidMount() {
    // this.setState((currentDate = 'hello'));
  }

  render() {
    return (
      <div className="header-date">
        <p>{this.state.currentDate}</p>
      </div>

    );
  }
}

class Item extends react.Component {
  constructor(props) {
    super(props);
  }
  render() {
    return (
      <div className="item">

      </div>
    );
  }

}





export default App;
