import react from "react";
import '../styles/Home.css'
import { ButtonGroup, Button } from 'react-bootstrap'


//Item tags
const TAG_NAMES = ['To-Do', 'Events', 'Notes', 'Tasks']

const TEST_ITEMS = [
    { id: '0', tag: TAG_NAMES[1] },
    { id: '1', tag: TAG_NAMES[0] },
    { id: '2', tag: TAG_NAMES[2] }

]

var itemsToDisplay = []


class Home extends react.Component {
    constructor(props) {
        super(props);
        this.state = {
            tag: ''
        }

        this.setTag = this.setTag.bind(this);
    }

    //Called when a state changes
    componentDidUpdate() {
        this.retrieveItemsData();
    }

    //Retrieves item data based on the current tag
    retrieveItemsData() {
        itemsToDisplay = [];

        TEST_ITEMS.forEach(item => {
            if (item['tag'] == this.state.tag) {
                itemsToDisplay.push(item);
            }
        });
    }

    //Returns new item component
    getItems(item) {
        return <Item key={item.id} tag={item.tag} />;
    }

    //Set the current tag 
    setTag(event) {
        this.setState({ tag: event.target.name });
    }

    render() {
        return (
            <>
                <div className="navbar">
                    <ButtonGroup>
                        {TAG_NAMES.map(name => (<TagButton key={TAG_NAMES.indexOf(name)} name={name} onClick={this.setTag} />))}
                    </ButtonGroup>
                </div>
                <div className="items-container">
                    {itemsToDisplay ? itemsToDisplay.map(item => this.getItems(item)) : null}
                </div>
            </>

        );
    }
}

//Returns a new tag button 
class TagButton extends react.Component {
    constructor(props) {
        super(props);
        this.name = props.name;
        this.handleClick = props.onClick;
    }

    render() {
        return (
            <Button name={this.name} className="mx-2 rounded-3" size="sm" variant="outline-info" onClick={this.handleClick}>{this.name}</Button>
        );
    }
}

//Item Component
class Item extends react.Component {
    constructor(props) {
        super(props);
        this.state = {
            tag: props.tag,
            title: props.title,
            desc: props.desc,

        };
    }

    render() {
        return (
            <div className="item">
                <div className="item-header">

                </div>
                <div className="item-main">

                </div>
                <div className="item-footer">

                </div>
            </div>
        );
    }
}

export default Home;
