import react from "react";
import '../styles/Home.css'
import { ButtonGroup, Button } from 'react-bootstrap'
import { propTypes } from "react-bootstrap/esm/Image";


//Item tags
const TAG_NAMES = ['To-Do', 'Events', 'Notes', 'Tasks']

const TEST_ITEMS = [{ tag: 'TO:DO' }, { tag: 'NOTES' }, { tag: 'TASKS' }]

class Home extends react.Component {
    constructor(props) {
        super(props);
        this.state = {
            tag: ''
        }

        this.setTag = this.setTag.bind(this);
    }

    //Retrieve items based on tag
    retrieveItems() {
        TEST_ITEMS.forEach(item => {
            if (item['tag'] == this.state.tag) {
                return <Item tag={item['tag']} />;
            }
        });
    }

    setTag(event) {
        let tag = event.target.name;
        setState({ tag: tag });
    }

    render() {
        return (
            <>
                <div className="navbar">
                    <ButtonGroup>
                        {TAG_NAMES.map(name => (<TagButton name={name} onClick={this.setTag} />))}
                    </ButtonGroup>
                </div>
                <div className="items-container">
                    {this.state.tag ? this.retrieveItems() : null}
                </div>
            </>

        );
    }
}

//Returns a new button with a given name
class TagButton extends react.Component {
    constructor(props) {
        super(props);
        this.name = props.name;
        this.handleClick = props.onClick;
    }

    render() {
        return (
            <Button name={this.name} className="mx-2 rounded-3" size="sm" variant="outline-info" onClick={this.handleClick}>{this.name}</Button>
        );
    }
}


class Item extends react.Component {
    constructor(props) {
        super(props);
        this.state = {
            tag: props.tag,
            title: props.title,
            desc: props.desc,

        };
    }

    render() {
        return (
            <div className="item">
                <div className="item-header">

                </div>
                <div className="item-main">

                </div>
                <div className="item-footer">

                </div>
            </div>
        );
    }
}

export default Home;
