import './styles/App.css';
import react from 'react';
import { Box } from '@mui/system';
import { Grid } from '@mui/material';

class App extends react.Component {

  constructor(props) {
    super(props)
  }

  render() {
    return (
      <div className="main-container">
        <Box padding="10px" display="flex" justifyContent="center" margin="10px">
          <Grid container spacing={2}>
            <Card value='1' />
            <Card value='2' />
            <Card value='3' />
            <Card value='4' />
          </Grid>
        </Box>
      </div >
    );
  }
}

class Card extends react.Component {

  constructor(props) {
    super(props);
    this.state = {
      value: props.value
    }
  }

  render() {
    return (
      <Grid item md={2}>
        {this.state['value']}
      </Grid>
    );
  }
}

export default App;
